/** @flow */
import LineByLine from 'n-readlines';
import { AUTO_GENERATED_STAMP } from '../constants';
import type { PathOsBased } from './path';

/**
 * check if file was auto generated by bit
 *
 * @param {PathOsBased} filePath - consumer path
 * @returns {boolean}
 */
export default function isFileAutoGenerated(filePath: PathOsBased): boolean {
  const liner = new LineByLine(filePath);
  const firstLine = liner.next().toString();
  return firstLine.includes(AUTO_GENERATED_STAMP);
}
